
public class Exam_05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int b = 20;
		
		/*
		 * 대입연산자 
		 * > 우항이 좌항으로 대입된다. (예외 X)
		 * > 좌항은 반드시 공간이어야만 한다. (변수)
		 * 
		 * A += B >> A = A + B >> A = 10 + 20 >> A = 30
		 * A -= B >> A = A - B >> A = 10 - 20 >> A = -10
		 * A *= B >> A = A * B >> A = 10 * 20 >> A = 200
		 * A /= B >> A = A / B >> A = 10 / 20 >> A = 0
		 * A %= B >> A = A % B >> A = 10 % 20 >> A = 10
		 * 
		 * 전치형 증감연산자
		 * ++A >>
		 * --A >>
		 * 후치형 증감연산자
		 * A++ >> A = A + 1 >> A += 1
		 * A-- >> A = A - 1 >> A -= 1
		 * 
		 * 관계연산자
		 * A == B >> A와 B는 같다.
		 * A != B >> A와 B는 같지 않다.
		 * A > B  >> A가 B보다 크다.
		 * A < B  >> A가 B보다 작다.
		 * A >= B >> A가 B보다 크거나 같다.
		 * A <= B >> A가 B보다 작거나 같다.
		 * 
		 * 논리 연산자 >> 숏컷 연산을 수행한다.
		 * ! >> NOT
		 * && >> AND
		 *   T && T = T
		 *   T && F = F
		 *   F && T = F
		 *   F && F = F
		 * || >> OR
		 *   T || T = T
		 *   T || F = T
		 *   F || T = T
		 *   F || F = T
		 * 
		 */
	}

}
