
public class Exam_04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10;
		int b = 20;
		
		System.out.println(a + " : " + b);
		
		// swap 기법
		// tmp = temporary (일시적인)
		int tmp = a;
		a = b;
		b = tmp;
		
		System.out.println(a + " : " + b);

	}

}
