package custom;

public class Calc {

	public static int plus(int i, int j) {
		// TODO Auto-generated method stub
		return i + j;
	}

	public int minus(int i, int j) {
		// TODO Auto-generated method stub
		return i - j;
	}

	public static int mply(int i, int j) {
		// TODO Auto-generated method stub
		return i * j;
	}

	public double divide(int i, int j) {
		// TODO Auto-generated method stub
		return i / (double) j;
	}

}
